import repositoryrepo = repository.Repository()__debug__ = trueassert repo.maxid == 0items = repo.get_all()assert itemsassert items.length == 0customer = { }customer.name = "Customer 1"repo.add(customer)assert repo.maxid == 1assert customer.id == 1items = repo.get_all()assert itemsassert items.length == 1assert items[0].name == "Customer 1"assert items[0].id == 1